package learnlooping;

public class Textinloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 for(int a=0 ;a<6; a++) 
		 {
         for(int z=65 ; z<68 ;z++) 
         {
        	 System.out.print((char)z+" ");

}        
         System.out.println();

		}
	}

}
